import combineReducers from 'redux';
import {
    REQUEST_POSTS_LIST,
    REQUEST_SINGLE_POST,
    RECEIVE_POSTS_LIST,
    RECEIVE_SINGLE_POST
} from '../actions/action';
import initState from './initState';
function parse (state,action){
    state={
        isFetching:false,
        url:undefined,
        items:[]
    }
    switch(action.name){
        case REQUEST_POSTS_LIST:
            return {...state,isFetching:true,url:action.url};
        case REQUEST_SINGLE_POST:
            return {...state,isFetching:true,url:action.url};
        case RECEIVE_POSTS_LIST:
            return {};
        case RECEIVE_SINGLE_POST:
            return {};
        default: 
            return {
            isFetching:false,
            action:false,
            url:false,
            items:[{
                type:'list',
                url:'http://www.motor1.com/rss/news/all',
                time:'00:00',
                error:initState.error,
                message:initState.msg||{
                    type:'error',
                    text:'Sorry XML file cannot be downloaded'
                },
                data:initState.data,
            }]
        }
    }
}

export default parse;
