import React from 'react';

class ImageLazyLoad extends React.Component{
    constructor(props){
        super(props)
        this.onLoad=this.onLoad.bind(this);

    }
    onLoad (item){
        var src=this.props.src,
            el=item.target,
            image=new Image();
            image.src=src;
            image.onload=()=>{
                el.src=src;
            }

    }
    render(){
        return (
            <img className={this.props.className} src={this.props.defaultImage} alt='' onLoad={this.onLoad} />
        )
    }
}
export default ImageLazyLoad;
