import React from 'react';
import {connect} from 'react-redux';

class Message extends React.Component{
    constructor(props){
        super(props);
        this.state={open:props.open};
        this.close=this.close.bind(this);
    }
    close(){
        this.setState({open:false})
    }
    render(){

        return (
            <div
            className={"message container row "+(this.state.open?"":"closed")}
            style={{width: '1080px', 'marginLeft': '135px',}}>
            <div className="message-content">
                <div className="message-icon">
                    <div className="fo fo-info" style={{display: 'none'}}></div>
                    <div className="fo fo-success" style={{display: 'none'}}></div>
                    <div className="fo fo-error" style={{display: 'block'}}></div>
                </div>
                <div className="message-text">Sorry XML file cannot be downloaded</div>
            </div>
            <div className="message-close" onClick={this.close}>
                <div className="fo fo-close"></div>
            </div>
        </div>
        )
    }
}
function mapStateToProps(state){
    const type=state.items[0].message.hasOwnProperty('type')?state.items[0].message.type:undefined,
          text=state.items[0].message.hasOwnProperty('text')?state.items[0].message.text:undefined;
    return {
        open:Boolean(state.items[0].message),
        type,
        text
    }
}
export default connect(mapStateToProps)(Message);
