import React from 'react';
import {connect} from 'react-redux';

class Posts extends React.Component{
    constructor(props){
        super(props);
        this.pagination.bind(this);
    }
    pagination(props){
        const posts=this.props.posts,
        postsPerRow=props.postsPerRow-1,
              postsRaw=posts.map((post,key)=>{
                    return (
                        <div className="post-container">
                                    <div className="post-time">
                                        <span className="fo fo-clock"></span>
                                        <span className="post-time-header">{post.pubDate}</span>
                                    </div>
                                    <div className="post-image-wrapper">
                                        <a
                                            className="img-post-link"
                                            href={post.link}>
                                            <img
                                                alt=''
                                                className="post-image"
                                                src={post.image}/></a>
                                    </div>

                                    <div className="post-content">
                                        <div className="post-title-wrapper">
                                            <a
                                                className="title-post-link"
                                                href={post.link}>
                                                <span className="post-title">{post.title}</span>
                                            </a>
                                        </div>

                                        <div className="post-description">
                                            <span>{post.description}</span>
                                        </div>
                                    </div>

                                    <div className="footer-post">
                                        <span className="fo fo-download"></span>
                                        <span className="fo fo-edit"></span>

                                    </div>
                            </div>

                    )
                });
        let postsLength=postsRaw.length-1,
            pointer=postsPerRow<=postsLength?postsPerRow:postsLength,
            postsCache=[];

        return postsRaw.filter((post,key) => {
            postsCache.push(post);
            if(pointer===key){
                const row=postsCache;
                pointer=postsLength>=(pointer+postsPerRow)?+(postsPerRow):postsLength;
                postsCache=[];
                return (
                    <div className='row'>{row}</div>
                )
            }
        });
    }

render(){
    return (
        <div className="result">
        <this.pagination  posts={this.posts} postsPerPage={3}/>
        </div>
    )
}
}

export default Posts;
