import React from 'react';
import {connect} from 'react-redux';

class InputForm extends React.Component{
    constructor(props){
        super(props);
        this.state={input:props.value||''}
    }
    render(){
        return (
            <div className="search container row">
            <form className="main-search" action="admin.php" method="GET">
                <input type="hidden" name="page" value="news-parser-main-menu"></input>
                <div className="row center">
                    <div className="input-wrapper">
                        <input className="search-textbox" type="text" name="rss" onChange={this.inputChange}></input>
                    </div>
                </div>
                <div className="row center">
                    <input className="main-button parse-rss-button" type="submit" value="Find RSS Feed" onSubmit={this.parseRss}></input>
                    <input className="main-button parse-page-button" type="submit" value="Parse Page" onSubmit={this.parseSinglePage}></input>
                </div>
            </form>
        </div>
        )
    }
}

export default connect ()(InputForm);
