import React from 'react';
import {connect} from 'react-redux';

class Indicator extends React.Component{
    constructor(props){
        super(props);
        this.state={position:-90}
        this.tick=this.tick.bind(this);
        this.animation=this.animation.bind(this)

    }
    tick(){
        const oldPosition=this.state.position;
        return {position:oldPosition>100?-90:oldPosition+this.props.step};

    }
    animation(){
        if(this.props.isAnimate){
        this.setState(this.tick());
        window.requestAnimationFrame(this.animation);
        }
    }
    componentWillReceiveProps(props){
        if(props.isAnimate){
            this.animation();
        }else if(!props.isAnimate){
            this.setState({position:-90});
        }

    }
    render(){

        const opacity=this.props.isAnimate?0:1,
              left=this.state.position+'%';
        return (
            <span className="download-indicator" style={{opacity,left}}></span>
        )
    }  
}
function mapStateToProps(state){
    return {
        isAnimate:state.parse.isFetching
    }
}

export default connect(mapStateToProps)(Indicator)
