import React from 'react';
import Image from './Image';
import {connect} from 'react-redux';
import {parsePost as parsePost_dev} from '../actions/action_dev';
import {parsePost as parsePost_prod} from '../actions/action';
import config from '../config';
const parsePost=config.mode==='production'?parsePost_prod:parsePost_dev;

function Post(props) {

    return (
        <div className={"post-container "+(props.status==="draft"?"post-parsed":"")}>
            <div className="post-time">
                <span className="fo fo-clock"></span>
                <span className="post-time-header">{props.pubDate}</span>
            </div>
            <div className="post-image-wrapper">
                <a
                    className="img-post-link"
                    href={props.link}>
                    <Image
                        className="post-image"

                        src={props.image}
                        />
                </a>
                </div>
                {
                    //<!--post-image-wrapper -->
                }
                <div className="post-content">
                    <div className="post-title-wrapper">
                        <a
                            className="title-post-link"
                            href={props.link}>
                            <span className="post-title">{props.title}</span>
                        </a>
                    </div>
                    {
                        //<!--post-title-wrapper -->
                    }
                    <div className="post-description">
                        <span>{props.description}</span>
                    </div>
                </div>
                {
                    //<!-- post-content -->
                }
                <div className="footer-post">
                    <span className="fo fo-download" onClick={()=>{props.parsePost(props.link,props.postId)}}></span>
                </div>
            </div>
    )
}
function mapStateToProps(state,props){
    return {
        status:state.parse.items.data[props._id].status
    }
}
function mapDispatchToProps(dispatch){
    return {
        parsePost:(url,id)=>{
            dispatch(parsePost(dispatch,url,id))
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Post);
