            return(
                <Post key={post._id.toString()} {...post} postId={post._id}  status={post.status}/>
            )
        })
    }
    row(posts,key){
        return (
            <div key={"row"+key.toString()} className="row">
            <this.renderPostsFromArray postsArray={posts} key />
            </div>
        )
    }
    render(){
        const posts=[],
              step=parseInt(this.props.row),
              end=this.props.posts.length;

        for (var i=0;end>i;i+=step){
            posts.push(this.row(this.props.posts.slice(i,i+step),i))
        }
        return posts;

    }
}

function mapStateToProps (state){
    const posts=state.parse.items.hasOwnProperty('data')?state.parse.items.data:[];
    return{
        posts:posts
    }
}

export default connect(mapStateToProps)(Posts)
