import {combineReducers} from 'redux';
import {
    REQUEST_POSTS_LIST,
    REQUEST_SINGLE_POST,
    RECEIVE_POSTS_LIST,
    RECEIVE_SINGLE_POST,
    SET_ROUTE
} from '../actions/action';
import initState from './initState';

function parse (state,action){
    state={
        isFetching:false,
        url:undefined,
        items:[]
    }
    switch(action.type){
        case REQUEST_POSTS_LIST:
            return {...state,isFetching:true,url:action.url};
        case REQUEST_SINGLE_POST:
            return {...state,isFetching:true,url:action.url};
        case RECEIVE_POSTS_LIST:
            return {...state,
                isFetching:false,
                url:action.url,
                error:action.posts.error,
                message:action.posts.msg,
                items:{
                    type:'list',
                    url:'http://www.motor1.com/rss/news/all',
                    date:action.date,

                    data:action.posts.data,
                }
            };
        case RECEIVE_SINGLE_POST:
            return {};
        default: 
            return {
            isFetching:false,
            url:false,
            message:false,
            error:0,
            items:{}
        }
    }
}
function route(state,action){
    state={
        isFetching:false,
        action:false,
        url:false,
    }
    if(action.type===SET_ROUTE){
        return {...state,action:action.action,url:action.url}
    }else{
        return {...state}
    }
}

export default combineReducers({parse,route});
