import React from 'react';
import {connect} from 'react-redux';
import {parsePost as parsePost_dev} from '../actions/action_dev';
import {parsePost as parsePost_prod} from '../actions/action';
import config from '../config';
const parsePost=config.mode==='production'?parsePost_prod:parsePost_dev;

class InputForm extends React.Component{
    constructor(props){
        super(props);
        this.state={inputValue:props.value||''};
        this.inputChange=this.inputChange.bind(this);
    }
    inputChange(event){
        this.setState({inputValue:event.target.value})
    }

    render(){
        return (
            <div className="search container row">
            <form className="main-search" action="admin.php" method="GET" >
                <input type="hidden" name="page" value="news-parser-main-menu"></input>
                <div className="row center">
                    <div className="input-wrapper">
                        <input className="search-textbox" type="text" name="url" value={this.state.inputValue} onChange={this.inputChange}></input>
                    </div>
                </div>
                <div className="row center">
                    <button className="main-button parse-rss-button" type="submit" name="action" value="list" >Parse RSS Feed</button>
                    <button className="main-button parse-page-button" type="button" onClick={()=>{this.props.parsePage(this.state.value)}}>Parse Page</button>
                </div>
            </form>
        </div>
        )
    }
}

function mapStateToProps(state){
    return {
        value:state.route.url||''
    }
}
function mapDispatchToProps(dispatch){
    return {
        parsePage:(url)=>{
            dispatch(parsePost(dispatch,url))
        }
    }
}

export default connect (mapStateToProps,mapDispatchToProps)(InputForm);
