import {combineReducers} from 'redux';
import {
    REQUEST_POSTS_LIST,
    REQUEST_SINGLE_POST,
    RECEIVE_POSTS_LIST,
    RECEIVE_SINGLE_POST,
    RECEIVE_ERROR,
    SET_ROUTE
} from '../actions/action';
import initState from './initState';

function parse (state,action){

    switch(action.type){
        case REQUEST_POSTS_LIST:
            return {...state,isFetching:true,url:action.url};
        case REQUEST_SINGLE_POST:
            return {...state,isFetching:true,url:action.url};
        case RECEIVE_POSTS_LIST:
            return {...state,
                isFetching:false,
                url:action.url,
                time:Date.now(),
                error:action.posts.err,
                message:action.posts.msg,
                action:'list',
                items:{
                    type:'list',
                    url:'http://www.motor1.com/rss/news/all',
                    date:action.date,   

                    data:action.posts.data.map((item,index)=>{
                        item._id=parseInt(index);
                        return item;
                    }),
                }
            };
        case RECEIVE_SINGLE_POST:
            if(action.post.data._id){
                state.items.data[action.post.data._id].status=action.post.data.status;
                state.items.data[action.post.data._id].postId=action.post.data.post_id;
                state.items.data[action.post.data._id].editLink=action.post.data.link;
            }
            return {...state,
                isFetching:false,
                url:action.url,
                time:Date.now(),
                action:'post',
                message:action.post.msg,
                error:action.post.err
            };
        case RECEIVE_ERROR:
            return{...state,
                isFetching:false,
                url:false,
                time:Date.now(),
                action:action.action,
                message:action.post.msg,
                error:action.post.error
            }
        default: 
            return {
            isFetching:false,
            url:false,
            message:false,
            error:0,
            items:{}
        }
    }
}
function route(state,action){
    state={
        isFetching:false,
        action:false,
        url:false,
    }
    if(action.type===SET_ROUTE){
        return {...state,action:action.action,url:action.url}
    }else{
        return {...state}
    }
}

export default combineReducers({parse,route});
