import React from 'react';
import Post from './Post';
import {connect} from 'react-redux';

class Posts extends React.Component{

    parsePost(url){

    }
    renderPostsFromArray(props){
        return props.postsArray.map(post=>{
            return(
                <Post {...props.postsArray} onClickDownload={this.parsePost} />
            )
        })
    }
    row(posts){
        return (
            <div key={"row"} className="row">
            <this.renderPostsFromArray postsArray={posts} />
            </div>
        )
    }
    render(){
        const posts=[],
              step=this.props.rowLength,
              end=this.props.posts.length;
        for (var i=0;end>i;i+=step){
            posts.push(this.row(this.props.posts.slice(i,i+step)))
        }
        return posts;

    }
}

function mapStateToProps (state){
    return{
        posts:state.items[0].data
    }
}

export default connect(mapStateToProps)(Posts)
