import React from 'react';
import {connect} from 'react-redux';
import {requestPostsList} from '../actions/action';
import {requestPost} from '../actions/action';

class InputForm extends React.Component{
    constructor(props){
        super(props);
        this.state={inputValue:props.value||''};
        this.inputChange=this.inputChange.bind(this);
    }
    inputChange(event){
        this.setState({inputValue:event.target.value})
    }

    render(){
        return (
            <div className="search container row">
            <form className="main-search" action="admin.php" method="GET">
                <input type="hidden" name="page" value="news-parser-main-menu"></input>
                <div className="row center">
                    <div className="input-wrapper">
                        <input className="search-textbox" type="text" name="rss" value={this.state.inputValue} onChange={this.inputChange}></input>
                    </div>
                </div>
                <div className="row center">
                    <input className="main-button parse-rss-button" type="submit" value="Find RSS Feed" onSubmit={this.props.parseRss}></input>
                    <input className="main-button parse-page-button" type="submit" value="Parse Page" onSubmit={this.props.parseSinglePage}></input>
                </div>
            </form>
        </div>
        )
    }
}

function mapStateToProps(state){
    return {
    }
}
function mapDispatchToProps(dispatch){
    return {
        parseRss:(event)=>{
            dispatch(requestPostsList(event.value))
        },
        parseSinglePage(event){
            dispatch(requestPost(event.value))
        }
    }
}

export default connect ()(InputForm);
