import React from 'react';
import {connect} from 'react-redux';
import imageLazyLoad from 'imageLazyLoad'

class Posts extends React.Component{
    constructor(props){
        super(props);
        this.pagination.bind(this);
    }
    pagination(props){
        const posts=props.posts,
        postsPerRow=props.postsPerRow-1,
              postsRaw=posts.map((post,key)=>{
                    return (
                        <div key={key.toString()} className="post-container">
                                    <div className="post-time">
                                        <span className="fo fo-clock"></span>
                                        <span className="post-time-header">{post.pubDate}</span>
                                    </div>
                                    <div className="post-image-wrapper">
                                        <a
                                            className="img-post-link"
                                            href={post.link}>
                                            <imageLazyLoad
                                                alt=''
                                                className="post-image"
                                                src={post.image}
                                                defaultImage='/public/image/Grey-Gradient.png'/></a>
                                    </div>

                                    <div className="post-content">
                                        <div className="post-title-wrapper">
                                            <a
                                                className="title-post-link"
                                                href={post.link}>
                                                <span className="post-title">{post.title}</span>
                                            </a>
                                        </div>

                                        <div className="post-description">
                                            <span>{post.description}</span>
                                        </div>
                                    </div>

                                    <div className="footer-post">
                                        <span className="fo fo-download"></span>
                                        <span className="fo fo-edit"></span>

                                    </div>
                            </div>

                    )
                });
        let postsLength=postsRaw.length-1,
            pointer=postsPerRow<=postsLength?postsPerRow:postsLength,
            postsCache=[],
            result=[]; 
       postsRaw.forEach((post,key) => {
            postsCache.push(post);
            if(pointer===key){
                const row=postsCache;
                pointer=postsLength>=(pointer+postsPerRow+1)?pointer+(postsPerRow+1):postsLength;
                postsCache=[];
                result.push (
                    <div key={'row'+key.toString()} className='row'>{row}</div>
                )
            }
        });
        return result;
    }

render(){
    return (
        <div  className="result">
        <this.pagination  posts={this.props.posts} postsPerRow={3}/>
        </div>
    )
}
}

function stateToProps(state){
    return {
        posts:state.items[0].data
    }
}


export default connect(stateToProps)(Posts);
