import React from 'react';
import {connect} from 'react-redux';
import action from '../actions/action'

class Message extends React.Component{
    constructor(props){
        super(props);
        this.state={open:props.open};
        this.close=this.close.bind(this);
        this.link=this.link.bind(this);
    }
    close(){
        this.setState({open:false})
    }
    componentDidUpdate(prevProps, prevState){
        if(this.props!==prevProps){
            this.setState({open:this.props.open})
        }

    }
    link(props){
        const link=[]
        if(this.props.link){
            link.push(<a href={props.link} className={props.className} onClick={(event)=>{
                event.preventDefault();
                const newWindow=window.open(event.target.href,'_blank');
                newWindow.focus();
            }}>link</a>)
        }
        return link;
    }
    render(){

        return (
            <div
            className={"message container row "+(this.state.open?"":"closed")}
            style={{width: '1080px', 'marginLeft': '135px',}}>
            <div className="message-content">
                <div className="message-icon">
                    <div className="fo fo-info" style={this.props.type==="info"?{display: 'block'}:{display: 'none'}}></div>
                    <div className="fo fo-success" style={this.props.type==="success"?{display: 'block'}:{display: 'none'}}></div>
                    <div className="fo fo-error" style={this.props.type==="error"?{display: 'block'}:{display: 'none'}}></div>
                </div>
                <div className="message-text">
                    {this.props.text}
                    <this.link link={this.props.link} ></this.link>
                </div>
            </div>
            <div className="message-close" onClick={this.close}>
                <div className="fo fo-close"></div>
            </div>
        </div>
        )
    }
}
function mapStateToProps(state){

    const type=state.parse.message?state.parse.message.type:undefined,
          text=state.parse.message?state.parse.message.text:undefined,
          link=state.parse.message?state.parse.message.link:undefined,
          timestamp=state.parse.time;
    return {
        open:Boolean(state.parse.message),
        type,
        text,
        link
    }
}
export default connect(mapStateToProps)(Message);
