import React from 'react';
import ImageLazyLoad from '../components/ImageLazyLoad';
import defaultImage from '../image/Grey-Gradient.png';

function Post(props) {

    return (
        <div className="post-container">
            <div className="post-time">
                <span className="fo fo-clock"></span>
                <span className="post-time-header">{props.pubDate}</span>
            </div>
            <div className="post-image-wrapper">
                <a
                    className="img-post-link"
                    href={props.link}>
                    <ImageLazyLoad
                        className="post-image"
                        defaultImage={defaultImage}
                        src={props.image}
                        />
                </a>
                </div>
                {
                    //<!--post-image-wrapper -->
                }
                <div className="post-content">
                    <div className="post-title-wrapper">
                        <a
                            className="title-post-link"
                            href={props.link}>
                            <span className="post-title">{props.title}</span>
                        </a>
                    </div>
                    {
                        //<!--post-title-wrapper -->
                    }
                    <div className="post-description">
                        <span>{props.description}</span>
                    </div>
                </div>
                {
                    //<!-- post-content -->
                }
                <div className="footer-post">
                    <span className="fo fo-download" onClick={()=>{props.onClickDownload(props.link)}}></span>
                </div>
            </div>
    )
}

export default Post;
