import initialState from '../reducers/initState';


export const REQUEST_POSTS_LIST='REQUEST_POSTS_LIST';
export const REQUEST_SINGLE_POST='REQUEST_SINGLE_POST';
export const RECEIVE_POSTS_LIST='RECEIVE_POSTS_LIST';
export const RECEIVE_SINGLE_POST='RECEIVE_SINGLE_POST';
export const RECEIVE_ERROR='RECEIVE_ERROR';
export const SET_ROUTE='SET_ROUTE';

export function requestPostsList (url){
    return {
        type:REQUEST_POSTS_LIST,
        url:url
    }
}
export function setRoute(params){
    return {
        type:SET_ROUTE,
        action:params.action||false,
        url:params.url?decodeURIComponent(params.url):false
    }
}
export function requestPost (url) {
    return {
        type:REQUEST_SINGLE_POST,
        url:url
    }
}
export function receivePostsList (url,posts) {
    return {
        type:RECEIVE_POSTS_LIST,
        url:url,
        posts:posts,
        date:Date.now()
    }
}
export function receiveError(url,data){
    return{
        type:RECEIVE_ERROR,
        url:url,
        post:data
    }
}
export function receivePost(url,post){
    return {
        type:RECEIVE_SINGLE_POST,
        url:url,
        post,
        date:Date.now()
    }
}

export function fetchPostsList(dispatch,url){
    dispatch(requestPostsList(url))
    return dispatch=>{
        return fetch('admin-ajax.php?action=parsing_get_list&url='+encodeURIComponent(url),
        {method: 'POST',
         headers: {
           'Accept': 'application/json',
           'Content-Type': 'application/json'
         }
        })
        .then(response=>response.json())
        .then((json,error)=>{
            error&&console.error(error);
            if(!json.err){
            dispatch(receivePostsList (url,json))
            }else{
                dispatch(receiveError(url,json));
            }
        })
    }
}
export function parsePost(dispatch,url,id){
    dispatch(requestPost(url));
    return dispatch=>{fetch ('admin-ajax.php?action=parsing_api&url='+encodeURIComponent(url),
    {method: 'POST',
     headers: {
       'Accept': 'application/json',
       'Content-Type': 'application/json'
     }
    })
    .then(response=>response.json())
    .then((json,error)=>{
        error&&console.error(error);
        if(!json.err){
            json.data._id=id;
            dispatch(receivePost(url,json));
        }else{
            dispatch(receiveError(url,json));
        }
    })}
}
